const sections = ["Summary", "Conventions", "Motivation", "Design", "Attribution", "Drawbacks", "Alternatives", "Unresolved"]

function convert (record, index) {
  record.id = [
    `${index}`.padStart(4, "0"),
    ...record.title.split(" "),
  ].join("-")
  record.topics = [
    Math.random() >= 0.9 ? "auth": "",
    Math.random() >= 0.8 ? "language": "",
    Math.random() >= 0.7 ? "library": "",
    Math.random() >= 0.5 ? "pattern": "",
    Math.random() >= 0.9 ? "process": "",
    Math.random() >= 0.8 ? "runtime": "",
  ].filter(Boolean)

  return [idFn(index, record.title), mdFn(record)]
}

const idFn = (index, title) =>[
  index.toString().padStart(4, "0"),
  ...title.split(" "),
].join("-")
const mdFn = ({content, replaces, replacedBy, title, topics}) => `
# ${title}

  * Discussion: none
  * Replaces: ${replaces}
  * Replaced by: ${replacedBy}
  * Topics: ${topics.join(", ")}

${content
  .split(/\n+/)
  .map((text, index) => `## ${sections[index]}\n\n${Array(4).fill().join(text)}`)
  .join("\n\n")}
`
